import { jsPDF } from 'jspdf';
export const PDFGenerator = {

    // Configuraci√≥n de branding premium
    config: {
        companyName: 'GANBATTE',
        companyLogo: 'data:image/jpeg;base64,',
        primaryColor: [30, 41, 59],      // Azul oscuro m√°s elegante
        secondaryColor: [14, 165, 233],  // Cyan vibrante
        accentColor: [34, 197, 94],      // Verde esmeralda
        gradientStart: [30, 41, 59],     // Para gradientes
        gradientEnd: [15, 23, 42],       // Azul m√°s oscuro
        contactInfo: {
            phone: '+549 11 4334-7600',
            email: 'info@ganbatte.com.ar',
            website: 'www.ganbatte.com.ar',
            address: 'Av. Belgrano 835 piso 2 Of D, Caba , Buenos aires.'
        }
    },

    setConfig(newConfig) {
    this.config = { ...this.config, ...newConfig };
},

getMonedaSimbolo(cotizacion) {
    const simbolos = {
        'USD': 'US$', 'ARS': '$', 'EUR': '‚Ç¨', 'JPY': '¬•', 'GBP': '¬£', 
        'BRL': 'R$', 'CNY': '¬•', 'CHF': 'CHF', 'CAD': 'C$', 'AUD': 'A$', 
        'MXN': 'MX$', 'CLP': 'CLP$',
    };
    const codigo = cotizacion?.moneda?.toUpperCase() || 'USD';
    return { codigo: codigo, simbolo: simbolos[codigo] || '$' };
},

formatNumber(number, decimals = 2) {
    if (typeof number !== 'number') {
        number = parseFloat(number) || 0;
    }
    return number.toLocaleString('es-ES', {
        minimumFractionDigits: decimals,
        maximumFractionDigits: decimals,
    });
},

generar({ cotizacion, costos, totales, tipo, seccionesHabilitadas = {} }) {
    const doc = new jsPDF(); 
    doc.setFont('helvetica', 'normal');
    
    // M√©tricas de c√°lculo
    const isLclMaritimo = cotizacion?.modo_transporte === "Maritima LCL" || 
                         (cotizacion?.modo_transporte === "Maritima" && cotizacion?.tipo_carga === "LCL");
    const tons = (cotizacion?.peso_total_kg || 0) / 1000;
    const cbm = cotizacion?.volumen_m3 || 0;
    const revenueTon = Math.max(tons, cbm);
    const pesoCargable = cotizacion?.peso_cargable_kg || 0;

    const { codigo: codigo_moneda, simbolo: simbolo_moneda } = this.getMonedaSimbolo(cotizacion);

    const metricas = { 
        isLclMaritimo, tons, cbm, revenueTon, pesoCargable, codigo_moneda, simbolo_moneda 
    };

    if (tipo === 'interno') {
        this.generarPDFInterno(doc, cotizacion, costos, totales, seccionesHabilitadas, metricas);
    } else {
        this.generarPDFClientePremium(doc, cotizacion, costos, totales, seccionesHabilitadas, metricas);
    }
    
    this.agregarNumeracionDePaginas(doc);

    const nombreArchivo = `Cotizacion_${tipo === 'interno' ? 'Interna' : 'Cliente'}_${cotizacion.codigo_legible || cotizacion.codigo}.pdf`;
    doc.save(nombreArchivo);
    return doc.output('blob');
},

agregarNumeracionDePaginas(doc) {
    const totalPages = doc.internal.getNumberOfPages();
    for (let i = 1; i <= totalPages; i++) {
        doc.setPage(i);
        doc.setFontSize(8);
        doc.setTextColor(150, 150, 150);
        doc.text(`P√°gina ${i} de ${totalPages}`, 195, 285, { align: 'right' });
    }
},

// =================================================================
// >>>>> PDF CLIENTE PREMIUM - DISE√ëO SUPER PROFESIONAL <<<<<
// =================================================================

generarPDFClientePremium(doc, cotizacion, costos, totales, seccionesHabilitadas, metricas) {
    let y = 0;
    
    // Header premium con gradiente
    y = this.agregarHeaderPremium(doc, cotizacion, y);
    
    // Secci√≥n de detalles con dise√±o de tarjetas
    y = this.agregarDetallesOperacionPremium(doc, cotizacion, y, metricas);
    
    // Servicios con dise√±o de tabla moderna
    y = this.agregarServiciosPremium(doc, costos, seccionesHabilitadas, y, metricas);
    
    // Total con dise√±o destacado
    y = this.agregarTotalPremium(doc, totales, y, metricas);
    
    // T√©rminos y condiciones elegantes
    this.agregarTerminosPremium(doc, y);
    
    // Footer premium
    this.agregarFooterPremium(doc);
},

agregarHeaderPremium(doc, cotizacion, y) {
    // DEFINIR ALTURAS BASADAS EN EL LOGO
    const targetLogoHeight = 35; 
    const marginY = 0;         
    const headerHeight = targetLogoHeight + (marginY * 2);

    // --- FONDO CON DEGRAD√â EXACTO ---
    const width = 210;

    // Colores del degrad√©
    const start = { r: 64, g: 81, b: 107 };   // #40516B
    const end   = { r: 0, g: 52, b: 102 };    // #003466

    const gradientStartX = 60;

    // 1Ô∏è‚É£ Fondo s√≥lido hasta el inicio del degrad√©
    doc.setFillColor(start.r, start.g, start.b);
    doc.rect(0, 0, gradientStartX, headerHeight, "F");

    // 2Ô∏è‚É£ Degrad√© suave desde gradientStartX hasta el final
    const gradientWidth = width - gradientStartX;

    for (let i = 0; i <= gradientWidth; i++) {
        const t = i / gradientWidth;
        const smoothT = t * t * (3 - 2 * t);

        const r = Math.round(start.r + (end.r - start.r) * smoothT);
        const g = Math.round(start.g + (end.g - start.g) * smoothT);
        const b = Math.round(start.b + (end.b - start.b) * smoothT);

        doc.setFillColor(r, g, b);
        doc.rect(gradientStartX + i, 0, 1, headerHeight, "F");
    }

    // BARRA LATERAL ACENTUADA
    doc.setFillColor(14, 165, 233);
    doc.rect(0, 0, 6, headerHeight, 'F');
    
    // Logo o inicial
    if (this.config.companyLogo) {
        try {
            const logoX = 8;        
            const logoW = 40;       
            const logoY = marginY;
            const logoH = targetLogoHeight;
            
            doc.addImage(this.config.companyLogo, 'PNG', logoX, logoY, logoW, logoH);
            
        } catch (e) {
            doc.setFillColor(14, 165, 233);
            doc.circle(25, headerHeight / 2, 15, 'F');
            doc.setTextColor(255, 255, 255);
            doc.setFontSize(12);
            doc.setFont('helvetica', 'bold');
            doc.text('TL', 25, (headerHeight / 2) + 3, { align: 'center' });
        }
    } else {
        doc.setFillColor(14, 165, 233);
        doc.circle(25, headerHeight / 2, 15, 'F');
        doc.setTextColor(255, 255, 255);
        doc.setFontSize(12);
        doc.setFont('helvetica', 'bold');
        doc.text('TL', 25, (headerHeight / 2) + 3, { align: 'center' });
    }
    
    // REPOSICIONAR CONTENIDO HORIZONTAL PARA CENTRADO VERTICAL
    const middleY = headerHeight / 2;
    
    // T√≠tulo principal
    doc.setTextColor(255, 255, 255);
    doc.setFontSize(18);
    doc.setFont('helvetica', 'bold');
    // Mostrar el nombre del cliente en lugar de "COTIZACI√ìN"
doc.text(cotizacion.cliente || 'COTIZACI√ìN', 60, middleY - 3);
    
    doc.setFontSize(8);
    doc.setFont('helvetica', 'normal');
    doc.text('Propuesta Comercial', 60, middleY + 2);
    
    // Badge de c√≥digo moderno
    const badgeH = 15;
    const offsetY = +8;
    const badgeY = (middleY - (badgeH / 2)) + offsetY;

    doc.setFillColor(34, 197, 94);
    doc.roundedRect(160, badgeY, 40, badgeH, 4, 4, 'F'); 

    doc.setTextColor(255, 255, 255);
    doc.setFontSize(8); 
    doc.setFont('helvetica', 'bold');

    doc.text('C√ìDIGO', 180, badgeY + 4, { align: 'center' });
    doc.text(cotizacion?.codigo_legible || cotizacion?.codigo || 'N/A', 180, badgeY + 11, { align: 'center' });
    
    doc.setTextColor(200, 200, 200);
    doc.setFont("helvetica", "bold"); 
    doc.setFontSize(10); 
    const dateY = middleY + (badgeH / 2) + 10;
    doc.text(`Emitido: ${new Date().toLocaleDateString('es-ES')}`, 110, dateY - 10); 
    doc.text(`V√°lido hasta: ${cotizacion.fecha_validez ? new Date(cotizacion.fecha_validez).toLocaleDateString('es-ES') : 'N/A'}`, 110, dateY -5); 

    doc.setTextColor(0, 0, 0);
    return headerHeight + 5;
},

agregarDetallesOperacionPremium(doc, cotizacion, y, metricas) {
    // T√≠tulo de secci√≥n
    doc.setFontSize(16);
    doc.setFont('helvetica', 'bold');
    doc.setTextColor(30, 41, 59);
    doc.text('Detalles de la Operaci√≥n', 20, y + 8);
    
    // L√≠nea decorativa
    doc.setDrawColor(14, 165, 233);
    doc.setLineWidth(2);
    doc.line(20, y + 10, 90, y + 10);
    y += 15;

    // Container principal
    doc.setFillColor(248, 250, 252);
    doc.setDrawColor(226, 232, 240);
    doc.roundedRect(15, y +5, 176, 56, 4, 4, 'FD');
    
    // Grid de 3 columnas
    const columnas = [
        { 
            titulo: 'INFORMACI√ìN DE RUTA', 
            x: 25, 
            items: [
                { label: 'Ruta', value: `${cotizacion.origen || 'N/A'} ‚Üí ${cotizacion.destino || 'N/A'}` },
                { label: 'POL', value: cotizacion.puerto_origen || cotizacion.origen || 'N/A' },
                { label: 'POD', value: cotizacion.puerto_destino || cotizacion.destino || 'N/A' },
                { label: 'Incoterm', value: cotizacion.incoterm_origen || cotizacion.incoterm || 'N/A' },
                { label: 'Cliente', value: cotizacion.cliente || 'N/A' }
            ]
        },
        { 
            titulo: 'DETALLES T√âCNICOS', 
            x: 85, 
            items: [
                { label: 'Operaci√≥n', value: String(cotizacion.tipo_operacion || 'N/A') },
                { label: 'Transporte', value: String(cotizacion.modo_transporte || 'N/A') },
                { label: 'Equipo', value: String(cotizacion.tipo_contenedor || 'N/A') },
                { label: 'Cantidad', value: String(cotizacion.cantidad_contenedores || 0) }
            ]
        },
        { 
            titulo: 'INFORMACI√ìN COMERCIAL', 
            x: 145, 
            items: [
                { label: 'Referencia', value: String(cotizacion.referencia || 'N/A') },
                { label: 'Estado', value: String(cotizacion.estado || 'N/A') },
                { label: 'Validez', value: cotizacion.fecha_validez ? new Date(cotizacion.fecha_validez).toLocaleDateString('es-ES') : 'N/A' },
                { label: 'D√≠as restantes', value: '' },
                { label: 'Email', value: String(cotizacion.email_cliente || 'N/A') }
            ]
        }
    ];

    // Agregar detalles t√©cnicos espec√≠ficos
    if (cotizacion.tipo_operacion?.includes('A')) {
        columnas[1].items.push(
            { label: 'Aerol√≠nea', value: String(cotizacion.aerolinea || 'N/A') },
            { label: 'Peso', value: `${this.formatNumber(metricas.pesoCargable, 0)} kg` }
        );
    } else if (cotizacion.tipo_operacion?.includes('M')) {
        columnas[1].items.push(
            { label: 'L√≠nea', value: String(cotizacion.linea_maritima || 'N/A') }
        );
        
        if (metricas.isLclMaritimo) {
            columnas[1].items.push(
                { label: 'Peso', value: `${this.formatNumber(metricas.tons, 3)} TN` },
                { label: 'Volumen', value: `${this.formatNumber(cotizacion.volumen_m3 || 0, 3)} m¬≥` }
            );
        } else {
            columnas[1].items.push(
                { label: 'Peso', value: `${this.formatNumber(cotizacion.peso_total_kg || 0, 0)} kg` },
                { label: 'Volumen', value: `${this.formatNumber(cotizacion.volumen_m3 || 0, 3)} m¬≥` }
            );
        }
    }

    // Calcular d√≠as restantes
    const fechaValidez = cotizacion.fecha_validez ? new Date(cotizacion.fecha_validez) : null;
    const hoy = new Date();
    let diasRestantes = 'N/A';
    let colorDias = [100, 116, 139];

    if (fechaValidez && fechaValidez > hoy) {
        const diffTime = fechaValidez - hoy;
        diasRestantes = Math.ceil(diffTime / (1000 * 60 * 60 * 24)).toString();
        if (parseInt(diasRestantes) <= 7) colorDias = [234, 88, 12];
    } else if (fechaValidez && fechaValidez <= hoy) {
        diasRestantes = 'Vencida';
        colorDias = [220, 38, 38];
    }
    
    columnas[2].items[3].value = diasRestantes;

    // Dibujar columnas
    doc.setFontSize(8);
    columnas.forEach((columna, colIndex) => {
        let currentY = y + 15;
        
        // T√≠tulo de columna
        doc.setFont('helvetica', 'bold');
        doc.setTextColor(14, 165, 233);
        doc.text(columna.titulo, columna.x, currentY);
        currentY += 8;
        
        // Items
        doc.setFont('helvetica', 'normal');
        
        columna.items.forEach((item, itemIndex) => {
            const stringValue = String(item.value || 'N/A');
            
            // Etiqueta
            doc.setTextColor(71, 85, 105);
            doc.setFont('helvetica', 'bold');
            doc.text(`${item.label}:`, columna.x, currentY);
            
            // Valor
            if (colIndex === 2 && itemIndex === 3) {
                doc.setTextColor(colorDias[0], colorDias[1], colorDias[2]);
            } else {
                doc.setTextColor(30, 41, 59);
            }
            
            doc.setFont('helvetica', 'normal');
            
            let displayValue = stringValue;
            if (stringValue.length > 20) {
                displayValue = stringValue.substring(0, 17) + '...';
            }
            
            doc.text(displayValue, columna.x + 25, currentY);
            currentY += 5;
        });
    });

    return y + 90;
},

agregarServiciosPremium(doc, costos, seccionesHabilitadas, y, metricas) {
    const simbolo = metricas.simbolo_moneda;
    const MARGEN_INFERIOR = 30;

    // T√≠tulo de secci√≥n - SUBIDO 12px
    doc.setFontSize(16);
    doc.setFont('helvetica', 'bold');
    doc.setTextColor(30, 41, 59);
    doc.text('Servicios Incluidos', 20, y - 12);
    
    // L√≠nea decorativa - SUBIDO 12px
    doc.setDrawColor(14, 165, 233);
    doc.setLineWidth(2);
    doc.line(20, y - 9, 75, y - 9);
    y += 3; // Ajustado de 15 a 3 para compensar

    // Agrupar costos
    const costosPorSeccion = costos.reduce((acc, costo) => {
        const seccion = costo.seccion || 'OTROS';
        if (!acc[seccion]) acc[seccion] = [];
        acc[seccion].push(costo);
        return acc;
    }, {});
    
    const seccionesAMostrar = Object.keys(seccionesHabilitadas).filter(
        seccion => seccionesHabilitadas[seccion] === true
    );
    
    // Servicios con valor
    seccionesAMostrar.forEach(seccion => {
        const costosSeccion = costosPorSeccion[seccion] || [];
        const costosConVenta = costosSeccion.filter(c => (parseFloat(c.venta) || 0) > 0);
        
        if (costosConVenta.length === 0) return;
        
        // L√≠mite ajustado para margen inferior
        if (y > 220) {
            doc.addPage();
            y = this.agregarHeaderPaginaSecundariaPremium(doc);
        }
        
        const totalSeccion = costosConVenta.reduce((sum, c) => sum + (parseFloat(c.venta) || 0), 0);
        
        // Header de secci√≥n moderno
        doc.setFillColor(30, 41, 59);
        doc.roundedRect(20, y, 170, 16, 4, 4, 'F');
        
        // Icono
        doc.setFillColor(255, 255, 255);
        doc.circle(30, y + 8, 3, 'F');
        
        doc.setTextColor(255, 255, 255);
        doc.setFontSize(10);
        doc.setFont('helvetica', 'bold');
        doc.text(seccion.toUpperCase(), 40, y + 7);
        
        doc.setFontSize(9);
        doc.setTextColor(200, 200, 200);
        doc.text(`${costosConVenta.length} servicio${costosConVenta.length !== 1 ? 's' : ''}`, 40, y + 12);
        
        doc.setFont('helvetica', 'bold');
        doc.setTextColor(34, 197, 94);
        doc.text(`${simbolo} ${this.formatNumber(totalSeccion)}`, 175, y + 10, { align: 'right' });        
        y += 20;
        
        // Tabla de servicios
        costosConVenta.forEach((costo, idx) => {
            // L√≠mite ajustado para margen inferior
            if (y > 260) {
                doc.addPage();
                y = this.agregarHeaderPaginaSecundariaPremium(doc);
            }
            
            // Fondo alternado
            if (idx % 2 === 0) {
                doc.setFillColor(248, 250, 252);
            } else {
                doc.setFillColor(255, 255, 255);
            }
            doc.roundedRect(25, y, 160, 12, 4, 4, 'F');
            
            // Concepto
            doc.setTextColor(30, 41, 59);
            doc.setFontSize(8);
            doc.setFont('helvetica', 'normal');
            
            let conceptoTexto = costo.concepto;
            if (conceptoTexto.length > 60) {
                conceptoTexto = conceptoTexto.substring(0, 57) + '...';
            }
            doc.text(conceptoTexto, 30, y + 7);
            
            // Informaci√≥n de tarifa unitaria si existe
            let infoTarifa = '';
            const calcPor = costo.calculo_por;
            const calcPorWm = costo.calculo_por_wm;
            
            if (calcPor === 'CARGABLE' || calcPor === 'REAL') {
                const unitVenta = parseFloat(costo.venta_unitaria || '0') || 0;
                infoTarifa = `${simbolo}${this.formatNumber(unitVenta)}/${calcPor.replace('CARGABLE', 'KgC').replace('REAL', 'KgR')}`;
            } else if (calcPorWm === 'W/M' || calcPorWm === 'TN') {
                const unitVenta = parseFloat(costo.venta_unitaria_wm || '0') || 0;
                infoTarifa = `${simbolo}${this.formatNumber(unitVenta)}/${calcPorWm.replace('W/M', 'RT').replace('TN', 'TN')}`;
            }
            
            if (infoTarifa) {
                doc.setFontSize(7);
                doc.setTextColor(100, 116, 139);
                doc.text(infoTarifa, 30, y + 11);
            }
            
            // Precio
            doc.setFontSize(9);
            doc.setFont('helvetica', 'bold');
            doc.setTextColor(30, 41, 59);
doc.text(`${simbolo} ${this.formatNumber(parseFloat(costo.venta) || 0)}`, 175, y + 8, { align: 'right' });
            
            y += 14;
        });
        
        y += 8;
    });

    // Secciones no incluidas
    const seccionesSinVenta = seccionesAMostrar.filter(seccion => {
        const costosSeccion = costosPorSeccion[seccion] || [];
        return !costosSeccion.some(costo => (parseFloat(costo.venta) || 0) > 0);
    });

    if (seccionesSinVenta.length > 0) {
        // L√≠mite ajustado para margen inferior
        if (y > 240) {
            doc.addPage();
            y = this.agregarHeaderPaginaSecundariaPremium(doc);
        }
        
        y += 10;
        
        doc.setFontSize(12);
        doc.setFont('helvetica', 'bold');
        doc.setTextColor(148, 163, 184);
        doc.text('Servicios No Incluidos', 105, y, { align: 'center' });
        y += 8;
        
        seccionesSinVenta.forEach((seccion, index) => {
            // L√≠mite ajustado para margen inferior
            if (y > 260) {
                doc.addPage();
                y = this.agregarHeaderPaginaSecundariaPremium(doc);
            }
            
            doc.setFillColor(248, 250, 252);
            doc.roundedRect(40, y, 130, 10, 6, 6, 'F');
            doc.setDrawColor(226, 232, 240);
            doc.roundedRect(40, y, 130, 10, 6, 6, 'S');
            
            doc.setFontSize(8);
            doc.setFont('helvetica', 'normal');
            doc.setTextColor(148, 163, 184);
            
            const seccionAjustada = seccion.length > 35 ? seccion.substring(0, 32) + '...' : seccion;
            doc.text(seccionAjustada, 50, y + 6);
            
            doc.setFont('helvetica', 'italic');
            doc.text('NO INCLUIDO', 160, y + 6, { align: 'right' });
            
            y += 14;
        });
    }
    
    // Agregar espacio extra al final antes del footer
    return y + 20;
},

agregarTotalPremium(doc, totales, y, metricas) {
    if (y > 220) {
        doc.addPage();
        y = this.agregarHeaderPaginaSecundariaPremium(doc);
    }
    
    const simbolo = metricas.simbolo_moneda;
    const codigo = metricas.codigo_moneda;
    
    // Tarjeta de total con gradiente
    doc.setFillColor(30, 41, 59);
    doc.roundedRect(40, y, 130, 40, 4, 4, 'F');
    
    // Borde de acento
    doc.setDrawColor(34, 197, 94);
    doc.setLineWidth(2);
    doc.roundedRect(40, y, 130, 40, 4, 4, 'S');
    
    doc.setTextColor(255, 255, 255);
    doc.setFontSize(16);
    doc.setFont('helvetica', 'bold');
    doc.text('TOTAL ESTIMADO', 105, y + 15, { align: 'center' });
    
    doc.setFontSize(20);
doc.text(`${simbolo} ${this.formatNumber(totales.venta)} ${codigo}`, 105, y + 30, { align: 'center' });
    
    doc.setTextColor(0, 0, 0);
    return y + 50;
},

agregarTerminosPremium(doc, y) {
    if (y > 200) {
        doc.addPage();
        y = this.agregarHeaderPaginaSecundariaPremium(doc);
    }
    
    // T√≠tulo
    doc.setFontSize(14);
    doc.setFont('helvetica', 'bold');
    doc.setTextColor(30, 41, 59);
    doc.text('Condiciones Comerciales', 20, y);
    
    // L√≠nea decorativa
    doc.setDrawColor(14, 165, 233);
    doc.setLineWidth(2);
    doc.line(20, y + 3, 90, y + 3);
    y += 12;
    
    // T√©rminos en tarjeta
    doc.setFillColor(248, 250, 252);
    doc.setDrawColor(226, 232, 240);
    doc.roundedRect(15, y, 180, 60, 8, 8, 'FD');
    
    const terminos = [
        '‚Ä¢ Precios expresados en la moneda indicada, sujetos a confirmaci√≥n final',
        '‚Ä¢ Servicios sujetos a disponibilidad al momento de la reserva formal',
        '‚Ä¢ No incluye gastos no especificados expl√≠citamente',
        '‚Ä¢ Tiempos de tr√°nsito son estimados y pueden variar',
        '‚Ä¢ Aplican t√©rminos y condiciones generales de transporte'
    ];
    
    doc.setFontSize(8);
    doc.setFont('helvetica', 'normal');
    doc.setTextColor(71, 85, 105);
    
    terminos.forEach((termino, index) => {
        const terminoY = y + 12 + (index * 8);
        doc.setFillColor(14, 165, 233);
        doc.circle(25, terminoY - 1.5, 1.5, 'F');
        doc.text(termino, 30, terminoY);
    });
},

agregarHeaderPaginaSecundariaPremium(doc) {
    // Header minimalista
    doc.setFillColor(30, 41, 59);
    doc.rect(0, 0, 210, 25, 'F');
    
    doc.setFillColor(14, 165, 233);
    doc.rect(0, 0, 5, 25, 'F');
    
    doc.setTextColor(255, 255, 255);
    doc.setFontSize(10);
    doc.setFont('helvetica', 'bold');
    
    doc.setFontSize(8);
    doc.text('', 195, 14, { align: 'right' });
    
    doc.setTextColor(0, 0, 0);
    return 35;
},

agregarFooterPremium(doc) {
    const pageCount = doc.internal.getNumberOfPages();
    const footerHeight = 15;
    const width = 210;

    // Colores degrad√© azul
    const start = { r: 64, g: 81, b: 107 };
    const end   = { r: 0, g: 52, b: 102 };

    for (let i = 1; i <= pageCount; i++) {
        doc.setPage(i);

        // Fondo degradado
        const gradientWidth = width;
        for (let x = 0; x < gradientWidth; x++) {
            const t = x / gradientWidth;
            const smoothT = t * t * (3 - 2 * t);

            const r = Math.round(start.r + (end.r - start.r) * smoothT);
            const g = Math.round(start.g + (end.g - start.g) * smoothT);
            const b = Math.round(start.b + (end.b - start.b) * smoothT);

            doc.setFillColor(r, g, b);
            doc.rect(x, 275, 1, footerHeight, "F");
        }

        // Informaci√≥n de contacto
        doc.setFontSize(7);
        doc.setTextColor(255, 255, 255);
        doc.setFont("helvetica", "bold");

        doc.text(this.config.companyName, 105, 280, { align: 'center' });
        doc.text(
            `${this.config.contactInfo.phone} ‚Ä¢ ${this.config.contactInfo.email} ‚Ä¢ ${this.config.contactInfo.website}`,
            105,
            284,
            { align: 'center' }
        );
        doc.text(this.config.contactInfo.address, 105, 288, { align: 'center' });

        // N√∫mero de p√°gina
        doc.setFontSize(8);
        doc.setFont("helvetica", "bold");
        doc.setTextColor(255, 255, 255);
        doc.text(`P√°gina ${i} de ${pageCount}`, width - 10, 288, { align: 'right' });
    }
},

// =================================================================
// >>>>> M√âTODOS PARA EL PDF INTERNO (CON HEADER PREMIUM) <<<<<
// =================================================================

generarPDFInterno(doc, cotizacion, costos, totales, seccionesHabilitadas, metricas) {
    let y = 0;
    
    // Usar el mismo header premium
    y = this.agregarHeaderPremium(doc, cotizacion, y);
    
    // Agregar marca de documento interno
    y = this.agregarMarcaInterna(doc, y);
    
    // Secci√≥n de detalles
    y = this.agregarDetallesOperacionInterna(doc, cotizacion, y, metricas);
    
    // Gastos con informaci√≥n de costos y m√°rgenes
    y = this.agregarGastosHabilitados(doc, costos, seccionesHabilitadas, y, metricas);
    
    // Resumen financiero
    this.agregarResumenFinanciero(doc, totales, y, metricas);
    
    // Footer premium
    this.agregarFooterPremium(doc);
},

agregarMarcaInterna(doc, y) {
    // Marca de agua para documento interno
    doc.setFillColor(255, 243, 205);
    doc.setDrawColor(255, 193, 7);
    doc.roundedRect(50, y, 110, 12, 3, 3, 'FD');
    
    doc.setTextColor(133, 100, 4);
    doc.setFontSize(10);
    doc.setFont('helvetica', 'bold');
    doc.text('DOCUMENTO INTERNO - INFORMACI√ìN CONFIDENCIAL', 105, y + 7, { align: 'center' });
    
    doc.setTextColor(0, 0, 0);
    return y + 20;
},

agregarDetallesOperacionInterna(doc, cotizacion, y, metricas) {
    // T√≠tulo de secci√≥n
    doc.setFontSize(16);
    doc.setFont('helvetica', 'bold');
    doc.setTextColor(30, 41, 59);
    doc.text('Detalles de la Operaci√≥n - INTERNO', 20, y + 4);
    
    // L√≠nea decorativa
    doc.setDrawColor(14, 165, 233);
    doc.setLineWidth(2);
    doc.line(20, y + 6, 120, y + 6);
    y += 15;

    // Container principal
    doc.setFillColor(248, 250, 252);
    doc.setDrawColor(226, 232, 240);
    doc.roundedRect(15, y, 176, 60, 4, 4, 'FD');
    
    // Grid de 2 columnas para informaci√≥n interna
    const columnas = [
        { 
            titulo: 'INFORMACI√ìN OPERATIVA', 
            x: 25, 
            items: [
                { label: 'Ruta', value: `${cotizacion.origen || 'N/A'} ‚Üí ${cotizacion.destino || 'N/A'}` },
                { label: 'Cliente', value: cotizacion.cliente || 'N/A' },
                { label: 'Operaci√≥n', value: String(cotizacion.tipo_operacion || 'N/A') },
                { label: 'Transporte', value: String(cotizacion.modo_transporte || 'N/A') },
                { label: 'Incoterm', value: cotizacion.incoterm_origen || cotizacion.incoterm || 'N/A' }
            ]
        },
        { 
            titulo: 'DATOS FINANCIEROS', 
            x: 110, 
            items: [
                { label: 'Moneda', value: metricas.codigo_moneda },
                { label: 'Referencia', value: String(cotizacion.referencia || 'N/A') },
                { label: 'Validez', value: cotizacion.fecha_validez ? new Date(cotizacion.fecha_validez).toLocaleDateString('es-ES') : 'N/A' },
                { label: 'Estado', value: String(cotizacion.estado || 'N/A') },
                { label: 'C√≥digo', value: cotizacion.codigo_legible || cotizacion.codigo || 'N/A' }
            ]
        }
    ];

    // Agregar m√©tricas espec√≠ficas
    if (metricas.isLclMaritimo) {
        columnas[0].items.push(
            { label: 'Peso (TN)', value: `${this.formatNumber(metricas.tons, 3)}` },
            { label: 'Volumen (m¬≥)', value: `${this.formatNumber(metricas.cbm, 3)}` },
            { label: 'Revenue Ton', value: `${this.formatNumber(metricas.revenueTon, 3)}` }
        );
    } else {
        columnas[0].items.push(
            { label: 'Peso (kg)', value: `${this.formatNumber(cotizacion.peso_total_kg || 0, 0)}` },
            { label: 'Volumen (m¬≥)', value: `${this.formatNumber(cotizacion.volumen_m3 || 0, 3)}` }
        );
    }

    // Dibujar columnas
    doc.setFontSize(8);
    columnas.forEach((columna) => {
        let currentY = y + 15;
        
        // T√≠tulo de columna
        doc.setFont('helvetica', 'bold');
        doc.setTextColor(14, 165, 233);
        doc.text(columna.titulo, columna.x, currentY);
        currentY += 8;
        
        // Items
        doc.setFont('helvetica', 'normal');
        
        columna.items.forEach((item) => {
            const stringValue = String(item.value || 'N/A');
            
            // Etiqueta
            doc.setTextColor(71, 85, 105);
            doc.setFont('helvetica', 'bold');
            doc.text(`${item.label}:`, columna.x, currentY);
            
            // Valor
            doc.setTextColor(30, 41, 59);
            doc.setFont('helvetica', 'normal');
            
            let displayValue = stringValue;
            if (stringValue.length > 25) {
                displayValue = stringValue.substring(0, 22) + '...';
            }
            
            doc.text(displayValue, columna.x + 28, currentY);
            currentY += 5;
        });
    });

    return y + 80;
},

agregarGastosHabilitados(doc, costos, seccionesHabilitadas, yInicio, metricas) {
    let y = yInicio + 5;
    const simbolo = metricas.simbolo_moneda;
    
    doc.setFontSize(14);
    doc.setFont('helvetica', 'bold');
    doc.setTextColor(30, 41, 59);
    doc.text('DETALLE DE GASTOS - INFORMACI√ìN INTERNA', 105, y, { align: 'center' });
    y += 10;
    
    const costosPorSeccion = costos.reduce((acc, costo) => {
        const seccion = costo.seccion || 'OTROS';
        if (!acc[seccion]) acc[seccion] = [];
        acc[seccion].push(costo);
        return acc;
    }, {});
    
    const seccionesAMostrar = Object.keys(seccionesHabilitadas).filter(
        seccion => seccionesHabilitadas[seccion] === true
    );
    
    const seccionesConCostos = [];
    const seccionesSinCostos = [];
    
    seccionesAMostrar.forEach(seccion => {
        const costosSeccion = costosPorSeccion[seccion] || [];
        if (costosSeccion.length > 0) {
            seccionesConCostos.push(seccion);
        } else {
            seccionesSinCostos.push(seccion);
        }
    });
    
    // SECCIONES CON GASTOS
    seccionesConCostos.forEach((seccion, index) => {
        if (y > 250) {
            doc.addPage();
            y = this.agregarHeaderPaginaSecundariaPremium(doc);
            y += this.agregarMarcaInternaPaginaSecundaria(doc, y);
        }
        
        const costosSeccion = costosPorSeccion[seccion];
        
        // Header de secci√≥n con informaci√≥n financiera
        doc.setFillColor(30, 41, 59);
        doc.roundedRect(15, y, 180, 14, 4, 4, 'F');
        
        doc.setTextColor(255, 255, 255);
        doc.setFontSize(10);
        doc.setFont('helvetica', 'bold');
        doc.text(seccion.toUpperCase(), 25, y + 8);
        
        // Totales de la secci√≥n
        const totalCosto = costosSeccion.reduce((sum, c) => sum + (parseFloat(c.costo) || 0), 0);
        const totalVenta = costosSeccion.reduce((sum, c) => sum + (parseFloat(c.venta) || 0), 0);
        const beneficio = totalVenta - totalCosto;
        const porcentaje = totalCosto > 0 ? (beneficio / totalCosto) * 100 : 0;
        
        doc.setFontSize(8);
        doc.text(`Costo: ${simbolo}${this.formatNumber(totalCosto)}`, 120, y + 5);
        doc.text(`Venta: ${simbolo}${this.formatNumber(totalVenta)}`, 120, y + 10);
        
        // Margen con color
        if (beneficio >= 0) {
            doc.setTextColor(34, 197, 94);
        } else {
            doc.setTextColor(239, 68, 68);
        }
        doc.text(`Margen: ${simbolo}${this.formatNumber(beneficio)} (${porcentaje.toFixed(1)}%)`, 170, y + 8, { align: 'right' });
        
        y += 18;
        
        // Encabezados de tabla
        doc.setTextColor(100, 116, 139);
        doc.setFont('helvetica', 'bold');
        doc.setFontSize(7);
        
        doc.text('CONCEPTO', 20, y);
        doc.text('COSTO UNIT.', 100, y, { align: 'right' });
        doc.text('VENTA UNIT.', 120, y, { align: 'right' });
        doc.text('COSTO TOTAL', 140, y, { align: 'right' });
        doc.text('VENTA TOTAL', 160, y, { align: 'right' });
        doc.text('MARGEN %', 180, y, { align: 'right' });
        
        y += 4;
        doc.setDrawColor(226, 232, 240);
        doc.line(15, y, 195, y);
        y += 6;

        // Detalle de costos
        costosSeccion.forEach((costo, idx) => {
            if (y > 270) {
                doc.addPage();
                y = this.agregarHeaderPaginaSecundariaPremium(doc);
                y += this.agregarMarcaInternaPaginaSecundaria(doc, y);
                
                // Redibujar encabezados
                doc.setTextColor(100, 116, 139);
                doc.setFont('helvetica', 'bold');
                doc.setFontSize(7);
                
                doc.text('CONCEPTO', 20, y);
                doc.text('COSTO UNIT.', 100, y, { align: 'right' });
                doc.text('VENTA UNIT.', 120, y, { align: 'right' });
                doc.text('COSTO TOTAL', 140, y, { align: 'right' });
                doc.text('VENTA TOTAL', 160, y, { align: 'right' });
                doc.text('MARGEN %', 180, y, { align: 'right' });
                
                y += 4;
                doc.setDrawColor(226, 232, 240);
                doc.line(15, y, 195, y);
                y += 6;
            }
            
            // Fondo alternado
            if (idx % 2 === 0) {
                doc.setFillColor(248, 250, 252);
            } else {
                doc.setFillColor(255, 255, 255);
            }
            doc.rect(15, y - 2, 180, 8, 'F');
            
            const costoValor = parseFloat(costo.costo) || 0;
            const ventaValor = parseFloat(costo.venta) || 0;
            const beneficioItem = ventaValor - costoValor;
            const porcentajeItem = costoValor > 0 ? (beneficioItem / costoValor) * 100 : 0;
            
            // Concepto
            doc.setFontSize(7);
            doc.setFont('helvetica', 'normal');
            doc.setTextColor(30, 41, 59);
            
            let conceptoTexto = costo.concepto;
            if (conceptoTexto.length > 45) {
                conceptoTexto = conceptoTexto.substring(0, 42) + '...';
            }
            doc.text(conceptoTexto, 20, y + 2);
            
            // Informaci√≥n de unidades
            let unidadCosto = '';
            let unidadVenta = '';
            const calcPor = costo.calculo_por;
            const calcPorWm = costo.calculo_por_wm;

            if (calcPor === 'CARGABLE' || calcPor === 'REAL') {
                const unitCosto = parseFloat(costo.costo_unitario || '0') || 0;
                const unitVenta = parseFloat(costo.venta_unitaria || '0') || 0;
                unidadCosto = `${this.formatNumber(unitCosto)}`;
                unidadVenta = `${this.formatNumber(unitVenta)}`;
            } else if (calcPorWm === 'W/M' || calcPorWm === 'TN') {
                const unitCosto = parseFloat(costo.costo_unitario_wm || '0') || 0;
                const unitVenta = parseFloat(costo.venta_unitaria_wm || '0') || 0;
                unidadCosto = `${this.formatNumber(unitCosto)}`;
                unidadVenta = `${this.formatNumber(unitVenta)}`;
            } else {
                unidadCosto = '-';
                unidadVenta = '-';
            }
            
            // Valores
            doc.text(`${simbolo}${unidadCosto}`, 100, y + 2, { align: 'right' });
            doc.text(`${simbolo}${unidadVenta}`, 120, y + 2, { align: 'right' });
            doc.text(`${simbolo}${this.formatNumber(costoValor)}`, 140, y + 2, { align: 'right' });
            doc.text(`${simbolo}${this.formatNumber(ventaValor)}`, 160, y + 2, { align: 'right' });
            
            // Margen con color
            if (porcentajeItem >= 0) {
                doc.setTextColor(34, 197, 94);
            } else {
                doc.setTextColor(239, 68, 68);
            }
            doc.text(`${porcentajeItem.toFixed(1)}%`, 180, y + 2, { align: 'right' });
            
            y += 8;
        });
        
        y += 10;
    });
    
    // SECCIONES SIN COSTOS
    if (seccionesSinCostos.length > 0) {
        if (y > 250) {
            doc.addPage();
            y = this.agregarHeaderPaginaSecundariaPremium(doc);
            y += this.agregarMarcaInternaPaginaSecundaria(doc, y);
        }
        
        y += 5;
        
        doc.setFontSize(11);
        doc.setFont('helvetica', 'bold');
        doc.setTextColor(148, 163, 184);
        doc.text('Secciones Habilitadas Sin Costos', 105, y, { align: 'center' });
        y += 8;
        
        seccionesSinCostos.forEach((seccion, index) => {
            if (y > 270) {
                doc.addPage();
                y = this.agregarHeaderPaginaSecundariaPremium(doc);
                y += this.agregarMarcaInternaPaginaSecundaria(doc, y);
            }
            
            doc.setFillColor(248, 250, 252);
            doc.roundedRect(40, y, 130, 8, 4, 4, 'F');
            doc.setDrawColor(226, 232, 240);
            doc.roundedRect(40, y, 130, 8, 4, 4, 'S');
            
            doc.setFontSize(8);
            doc.setFont('helvetica', 'normal');
            doc.setTextColor(148, 163, 184);
            
            const seccionAjustada = seccion.length > 35 ? seccion.substring(0, 32) + '...' : seccion;
            doc.text(seccionAjustada, 50, y + 5);
            
            doc.setFont('helvetica', 'italic');
            doc.text('SIN COSTOS', 160, y + 5, { align: 'right' });
            
            y += 12;
        });
    }
    
    return y + 10;
},

agregarMarcaInternaPaginaSecundaria(doc, y) {
    // Marca de agua para p√°ginas secundarias
    doc.setFillColor(255, 243, 205);
    doc.setDrawColor(255, 193, 7);
    doc.roundedRect(50, y, 110, 8, 2, 2, 'FD');
    
    doc.setTextColor(133, 100, 4);
    doc.setFontSize(8);
    doc.setFont('helvetica', 'bold');
    doc.text('DOCUMENTO INTERNO - CONFIDENCIAL', 105, y + 5, { align: 'center' });
    
    doc.setTextColor(0, 0, 0);
    return 12;
},

agregarResumenFinanciero(doc, totales, y, metricas) {
    if (y > 200) {
        doc.addPage();
        y = this.agregarHeaderPaginaSecundariaPremium(doc);
        y += this.agregarMarcaInternaPaginaSecundaria(doc, y);
    }

    const simbolo = metricas.simbolo_moneda;
    const codigo = metricas.codigo_moneda;
    
    // Container del resumen
    doc.setFillColor(30, 41, 59);
    doc.roundedRect(40, y, 130, 35, 4, 4, 'F');
    
    // Borde de acento
    doc.setDrawColor(14, 165, 233);
    doc.setLineWidth(2);
    doc.roundedRect(40, y, 130, 35, 4, 4, 'S');
    
    const beneficioTotal = totales.venta - totales.costo;
    const porcentajeTotal = totales.costo > 0 ? (beneficioTotal / totales.costo) * 100 : 0;
    
    doc.setTextColor(255, 255, 255);
    doc.setFontSize(14);
    doc.setFont('helvetica', 'bold');
    doc.text('RESUMEN FINANCIERO INTERNO', 105, y + 8, { align: 'center' });
    
    doc.setFontSize(10);
    doc.setFont('helvetica', 'normal');
    
    // Totales
    doc.text(`Total Costo: ${simbolo}${this.formatNumber(totales.costo)} ${codigo}`, 50, y + 18);
    doc.text(`Total Venta: ${simbolo}${this.formatNumber(totales.venta)} ${codigo}`, 50, y + 26);
    
    // Beneficio con color
    doc.setFont('helvetica', 'bold');
    if (beneficioTotal >= 0) {
        doc.setTextColor(34, 197, 94);
    } else {
        doc.setTextColor(239, 68, 68);
    }
    
    doc.text(`Beneficio: ${simbolo}${this.formatNumber(beneficioTotal)} ${codigo}`, 160, y + 18, { align: 'right' });
    doc.text(`Margen: ${porcentajeTotal.toFixed(1)}%`, 160, y + 26, { align: 'right' });
    
    doc.setTextColor(0, 0, 0);
}
};